model.dbsql_dbt_tpch.tempSumpFiBasicEps.raw_code:
{{
    config(
        materialized = 'table'
    )
}}
SELECT
  sk_companyid,
  fi_qtr_start_date,
  sum(fi_basic_eps) OVER (PARTITION BY companyid ORDER BY fi_qtr_start_date ROWS BETWEEN 4 PRECEDING AND CURRENT ROW) - fi_basic_eps sum_fi_basic_eps
FROM {{ ref('Financial') }}
JOIN {{ ref('DimCompany') }}
  USING (sk_companyid);

